public class Input {
    public void method() {
        if (true) {
            System.out.println("if");
        }
        else {
            System.out.println("else");
        }

        try {
            System.out.println("try");
        }
        catch (Exception e) {
            System.out.println("catch");
        }
        finally {
            System.out.println("finally");
        }
    }
}
